import React, { useEffect, useState } from 'react'
import { useParams, useHistory } from 'react-router-dom'
import { findIncicleById } from '../actions/IncicleFyndyID'
import { ListCards } from '../pages/CardsPage'

export function Cards() {
  const [card, setCard] = useState(null)
  const [photo, setPhoto] = useState(null)
  const { history } = useHistory()

  const { id } = useParams()
  const files = card.map(result => result.files)
  for (let file of files) {
    setPhoto(file)
  }

  useEffect(() => {
    async function fechData() {
      const { data, error } = await findIncicleById(id)
      if (error) {
        history.push('/')
      }
      setCard(data)
    }
    fechData()
  }, [history, id, photo])

  return <>{card && <ListCards card={card} photo={photo} />}</>
}
