import { Card, FormGroup, IconButton, Typography } from '@material-ui/core'
import { MoreHoriz } from '@material-ui/icons'
import React, { useEffect, useState } from 'react'

export function ListCards({ card }) {
  const [files, setFiles] = useState([0])
  // eslint-disable-next-line
  useEffect(() => {
    const files = card.map(result => result.files)
    for (let file of files) {
      setFiles(file)
    }
  })
  console.log(files)
  return (
    <>
      <div className='listcards'>
        <div>
          {card.map((e, i) => (
            <Card
              className='listbody'
              key={i}
              sx={{ mb: 2, width: '100%', ml: 2 }}
            >
              <FormGroup row>
                {files.map((e, i) => (
                  <div key={i}>
                    <img
                      alt=''
                      src={e.file}
                      style={{
                        width: '100px',
                        height: '100px',
                        margin: '10px',
                      }}
                    />
                  </div>
                ))}

                <div>
                  <Typography variant='subtitle1' sx={{ mt: 1 }}>
                    {e.title}
                  </Typography>
                  <div style={{ display: 'flex' }}>
                    {e.type === 'release' && (
                      <Typography
                        variant='body1'
                        sx={{ fontSize: 14, backgroundColor: 'blue' }}
                      >
                        {e.type}
                      </Typography>
                    )}
                    {e.type === 'event' && (
                      <Typography
                        variant='body1'
                        sx={{ fontSize: 14, backgroundColor: 'red' }}
                      >
                        {e.type}
                      </Typography>
                    )}
                    <Typography variant='body1' sx={{ fontSize: 14, ml: 1 }}>
                      {e.date}
                    </Typography>
                  </div>
                  <Typography varient='body2'>{e.description}</Typography>
                </div>
                <IconButton
                  size='small'
                  sx={{
                    backgroundColor: '#cdcdcd',
                    width: 30,
                    height: 30,
                    mt: 6,
                    ml: 20,
                  }}
                >
                  <MoreHoriz sx={{ fontSize: 15 }} />
                </IconButton>
              </FormGroup>
            </Card>
          ))}
        </div>
        <div style={{ flexDirection: 'column' }}>
          <Card sx={{ width: '250px', ml: 4, height: '60vh', mt: -5, mb: 5 }}>
            teste
          </Card>
          <Card sx={{ width: '250px', ml: 4, height: '60vh' }}>teste</Card>
        </div>
      </div>
    </>
  )
}
