import React, { useEffect, useState } from 'react'
import { useParams, useHistory } from 'react-router-dom'
import { findIncicleById } from '../actions/IncicleFyndyID'
import { ListCards } from '../pages/CardsPage'

export function Cards() {
  const [card, setCard] = useState([0])
  const [files, setFiles] = useState([0])
  const { history } = useHistory()

  const { id } = useParams()

  useEffect(() => {
    async function fechData() {
      const { file, data, error } = await findIncicleById(id)
      if (error) {
        history.push('/')
      }
      setCard(data)
      setFiles(file)
    }
    fechData()
  }, [history, id])

  return <>{card && <ListCards card={card} files={files} />}</>
}
