import React, { useState } from 'react'
import { IconButton, Tooltip, Menu, MenuItem } from '@material-ui/core'
import { MoreHoriz } from '@material-ui/icons'

const options = ['None']

const ITEM_HEIGHT = 48

export function MenuLang() {
  const [menu, setMenu] = useState(null)

  const open = Boolean(menu)
  const handleClick = event => {
    setMenu(event.currentTarget)
  }
  const handleClose = () => {
    setMenu(null)
  }
  return (
    <div>
      <Tooltip arrow title='Menu'>
        <IconButton
          aria-expanded={open ? 'true' : undefined}
          size='small'
          className='btnMore'
          sx={{
            backgroundColor: '#cdcdcd',
            width: 30,
            height: 30,
            mt: 6,
          }}
          onClick={{ handleClick }}
        >
          <MoreHoriz sx={{ fontSize: 15 }} />
        </IconButton>
      </Tooltip>
      <Menu
        id='long-menu'
        MenuListProps={{
          'aria-labelledby': 'long-button',
        }}
        anchorEl={menu}
        open={open}
        onClose={handleClose}
        PaperProps={{
          style: {
            maxHeight: ITEM_HEIGHT * 4.5,
            width: '20ch',
          },
        }}
      >
        {options.map(option => (
          <MenuItem
            key={option}
            selected={option === 'Pyxis'}
            onClick={handleClose}
          >
            {option}
          </MenuItem>
        ))}
      </Menu>
    </div>
  )
}
