import { Card, CardContent } from '@material-ui/core'
import React, { useEffect, useState } from 'react'
import { useParams, useHistory } from 'react-router-dom'
import { findIncicleById } from '../actions/IncicleFyndyID'
import imag4 from '../resurces/imag4.png'

export function CardsPage() {
  const [card, setCard] = useState(null)
  const { history } = useHistory()

  const { id } = useParams()

  useEffect(() => {
    async function fechData() {
      const { data, error } = await findIncicleById(id)
      if (error) {
        history.push('/')
      }
      setCard(data)
      console.log(data)
    }
    fechData()
  }, [history, id])

  return (
    <>
      <Card
        sx={{ width: '980px', display: 'flex', m: 5, flexDirection: 'column' }}
      >
        {card.map((e, i) => (
          <CardContent key={i} sx={{}}>
            <img
              src={imag4}
              alt=''
              style={{ width: '100px', height: '100px' }}
            />
            <p>{e.title}</p>
          </CardContent>
        ))}
      </Card>
    </>
  )
}
