import {
  Button,
  Card,
  CardContent,
  FormGroup,
  IconButton,
  Typography,
} from '@material-ui/core'
import { MoreHoriz } from '@material-ui/icons'
import React, { useEffect, useState } from 'react'
import axios from 'axios'

export function ListCards({ card }) {
  const [id] = useState(21)
  const [commons, setCommons] = useState({})
  const [files, setFiles] = useState([0])

  useEffect(() => {
    function photo() {
      const files = card.map(result => result.files)
      for (let file of files) {
        setFiles(file)
      }
    }
    photo()
  })
  useEffect(() => {
    axios
      .get(`http://localhost:3333/data/${id}`)
      .then(res => {
        setCommons(res.data.common.invited_people)
      })
      .catch(err => {
        console.log(err)
      })
  }, [id])
  console.log(commons)

  return (
    <>
      <div className='listcards'>
        <div>
          {card.map((e, i) => (
            <Card
              className='listbody'
              key={i}
              sx={{ mb: 4, width: '100%', ml: 2, mt: -2 }}
            >
              <FormGroup row>
                {files.map((e, i) => (
                  <div key={i}>
                    <img alt='foto' src={e.file} className='cube' />
                  </div>
                ))}

                <div>
                  <Typography variant='subtitle1' sx={{ mt: 1 }}>
                    {e.title}
                  </Typography>
                  <div style={{ display: 'flex' }}>
                    {e.type === 'release' && (
                      <>
                        <Typography
                          variant='body1'
                          sx={{
                            fontSize: 13,
                            backgroundColor: '#3489b1',
                            width: 100,
                            marginRight: '3px',
                            color: '#fff',
                            textAlign: 'center',
                            textTransform: 'uppercase',
                          }}
                        >
                          {e.type === 'release' && 'Comunicado'}
                        </Typography>
                        <Typography
                          variant='body1'
                          sx={{ fontSize: 13, marginTop: '1px' }}
                        >
                          {e.date}
                        </Typography>
                      </>
                    )}
                    {e.type === 'event' && (
                      <>
                        <Typography
                          variant='body1'
                          sx={{
                            fontSize: 13,
                            backgroundColor: '#ee8686',
                            paddingTop: '2px',
                            width: 55,
                            textAlign: 'center',
                            textTransform: 'uppercase',
                            marginRight: '3px',
                            marginTop: '2px',
                          }}
                        >
                          {e.type === 'event' && 'Evento'}
                        </Typography>
                        <Typography
                          variant='body1'
                          sx={{ fontSize: 13, paddingTop: '3px' }}
                        >
                          COSTELÃO CHURRASCARIA
                        </Typography>
                        |
                        <Typography
                          variant='body1'
                          sx={{ fontSize: 13, paddingTop: '3px' }}
                        >
                          {e.date}
                        </Typography>
                        |
                        {commons[0] &&
                          commons.map((invited_people, i) => (
                            <Typography
                              variant='body1'
                              color='primary'
                              sx={{
                                fontSize: 13,
                                marginTop: '3px',
                                textDecoration: 'underline',
                                cursor: 'pointer',
                              }}
                            >
                              {commons.invited_people &&
                                commons.invited_people.name}
                              {/* 3 CONFIRMAÇÕES DE {invited_people.name && invited_people.length} */}
                            </Typography>
                          ))}
                      </>
                    )}
                  </div>
                  <Typography varient='body2'>{e.description}</Typography>
                </div>
                <IconButton
                  size='small'
                  sx={{
                    backgroundColor: '#cdcdcd',
                    width: 30,
                    height: 30,
                    mt: 6,
                    ml: 20,
                  }}
                >
                  <MoreHoriz sx={{ fontSize: 15 }} />
                </IconButton>
              </FormGroup>
            </Card>
          ))}
        </div>
        <div style={{ flexDirection: 'column' }}>
          <CardContent
            sx={{
              width: '225px',
              height: '50vh',
              backgroundColor: '#fff2de',

              mt: -11,
              mb: 3,
              ml: 3,
              border: '1px solid #dec9a8',
            }}
          >
            <Typography variant='subtitle1' sx={{ color: 'inherit' }}>
              Endomarketing
            </Typography>
            <Typography
              paragraph={true}
              variant='body2'
              sx={{ fontSize: 13, mt: 3, color: 'cdcdcd' }}
            >
              Endomarketing está relacionado ás ações de treinamento ou
              qualificação dos colaboradores da empresa visando um melhor
              serviço para o cliente.Marketing interno, devido ao nome, é
              usualmente confundido com Endomarketing mesmo sendo conceitos
              diferentes.
            </Typography>
            <Button variant='outlined' color='inherit'>
              Dispensar
            </Button>
          </CardContent>
          <Card sx={{ width: '250px', ml: 4, height: '60vh' }}>
            {/* <DataFetingPhoto card={card} /> */}
          </Card>
        </div>
      </div>
    </>
  )
}
