import { Card, FormGroup, IconButton, Typography } from '@material-ui/core'
import { MoreHoriz } from '@material-ui/icons'
import React, { useEffect, useState } from 'react'

export function ListCards({ card }) {
  const [files, setFiles] = useState([0])

  useEffect(() => {
    function photo() {
      const files = card.map(result => result.files)
      for (let file of files) {
        setFiles(file)
      }
    }
    photo()
  })

  return (
    <>
      <div className='listcards'>
        <div>
          {card.map((e, i) => (
            <Card
              className='listbody'
              key={i}
              sx={{ mb: 2, width: '100%', ml: 2 }}
            >
              <FormGroup row>
                {files.map((e, i) => (
                  <div key={i}>
                    <img
                      alt='foto'
                      src={e.file}
                      style={{
                        width: '100px',
                        height: '100px',
                        margin: '10px',
                      }}
                    />
                  </div>
                ))}

                <div>
                  <Typography variant='subtitle1' sx={{ mt: 1 }}>
                    {e.title}
                  </Typography>
                  <div style={{ display: 'flex' }}>
                    {e.type === 'release' && (
                      <>
                        <Typography
                          variant='body1'
                          sx={{
                            fontSize: 13,
                            backgroundColor: '#3489b1',
                            width: 55,
                            marginRight: '3px',
                            color: '#fff',
                            textAlign: 'center',
                          }}
                        >
                          {e.type}
                        </Typography>
                        <Typography
                          variant='body1'
                          sx={{ fontSize: 13, marginTop: '1px' }}
                        >
                          {e.date}
                        </Typography>
                      </>
                    )}
                    {e.type === 'event' && (
                      <>
                        <Typography
                          variant='body1'
                          sx={{
                            fontSize: 13,
                            backgroundColor: 'red',
                            paddingTop: '2px',
                          }}
                        >
                          {e.type}
                        </Typography>
                        <Typography
                          variant='body1'
                          sx={{ fontSize: 13, paddingTop: '3px', pl: 1 }}
                        >
                          COSTELÃO CHURRASCARIA
                        </Typography>
                        |
                        <Typography
                          variant='body1'
                          sx={{ fontSize: 13, paddingTop: '3px' }}
                        >
                          {e.date}
                        </Typography>
                      </>
                    )}
                  </div>
                  <Typography varient='body2'>{e.description}</Typography>
                </div>
                <IconButton
                  size='small'
                  sx={{
                    backgroundColor: '#cdcdcd',
                    width: 30,
                    height: 30,
                    mt: 6,
                    ml: 20,
                  }}
                >
                  <MoreHoriz sx={{ fontSize: 15 }} />
                </IconButton>
              </FormGroup>
            </Card>
          ))}
        </div>
        <div style={{ flexDirection: 'column' }}>
          <Card sx={{ width: '250px', ml: 4, height: '60vh', mt: -5, mb: 5 }}>
            teste
          </Card>
          <Card sx={{ width: '250px', ml: 4, height: '60vh' }}>teste</Card>
        </div>
      </div>
    </>
  )
}
