import { Card, FormGroup, IconButton, Typography } from '@material-ui/core'
import { MoreHoriz } from '@material-ui/icons'
import React, { useEffect, useState } from 'react'

export function ListCards({ card }) {
  const [file, setFile] = useState([0])
  const files = card.map(result => result.files)

  for (let file of files) {
    setFile(file)
    console.log(file)
  }

  return (
    <>
      {card.map((e, i) => (
        <Card key={i} sx={{ mb: 2, width: '78%', ml: 2 }}>
          <FormGroup row>
            {file.map(file => (
              <img
                src={file.file}
                alt=''
                style={{
                  width: '100px',
                  height: '100px',
                  margin: '10px',
                }}
              />
            ))}

            <div>
              <Typography variant='subtitle1' sx={{ mt: 1 }}>
                {e.title}
              </Typography>
              <div style={{ display: 'flex' }}>
                {e.type === 'release' && (
                  <Typography
                    variant='body1'
                    sx={{ fontSize: 14, backgroundColor: 'blue' }}
                  >
                    {e.type}
                  </Typography>
                )}
                {e.type === 'event' && (
                  <Typography
                    variant='body1'
                    sx={{ fontSize: 14, backgroundColor: 'red' }}
                  >
                    {e.type}
                  </Typography>
                )}
                <Typography variant='body1' sx={{ fontSize: 14, ml: 1 }}>
                  {e.date}
                </Typography>
              </div>
              <Typography varient='body2'>{e.description}</Typography>
            </div>
            <IconButton
              size='small'
              sx={{
                backgroundColor: '#cdcdcd',
                width: 30,
                height: 30,
                mt: 6,
                ml: 20,
              }}
            >
              <MoreHoriz sx={{ fontSize: 15 }} />
            </IconButton>
          </FormGroup>
        </Card>
      ))}
    </>
  )
}
