                    {e.type === 'release' && (
                      <Typography
                        variant='body1'
                        sx={{ fontSize: 14, backgroundColor: 'blue' }}
                      >
                        {e.type}
                      </Typography>
                    )}
                    {e.type === 'event' && (
                      <Typography
                        variant='body1'
                        sx={{ fontSize: 14, backgroundColor: 'red' }}
                      >
                        {e.type}
                      </Typography>
                    )}
                    <Typography variant='body1' sx={{ fontSize: 14, ml: 1 }}>
                      {e.date}
                    </Typography>
                  </div>
                  <Typography varient='body2'>{e.description}</Typography>
                </div>
                <IconButton
                  size='small'
                  sx={{
                    backgroundColor: '#cdcdcd',
                    width: 30,
                    height: 30,
                    mt: 6,
                    ml: 20,
                  }}
                >
                  <MoreHoriz sx={{ fontSize: 15 }} />
                </IconButton>
              </FormGroup>
            </Card>
          ))}
        </div>
        <div style={{ flexDirection: 'column' }}>
          <Card sx={{ width: '250px', ml: 4, height: '60vh', mt: -5, mb: 5 }}>
            teste
